function changeScroll(){document.body.scrollTop>100||document.documentElement.scrollTop>100?($("#secondnav").show(),$("#nav").removeClass("shadow-sm")):($("#secondnav").hide(),$("#nav").addClass("shadow-sm"))}function setMap(){var address=$("#googleMap").data("address"),geocoder;address!=null&&address.length>0&&(geocoder=new google.maps.Geocoder,geocoder.geocode({address:address},function(results,status){if(status=="OK"){var map=new google.maps.Map(document.getElementById("googleMap"),{zoom:10,mapTypeControl:!1,streetViewControl:!1}),marker=new google.maps.Marker({map:map,position:results[0].geometry.location});map.setCenter(results[0].geometry.location)}else alert("Geocode was not successful for the following reason: "+status)}))}$(document).ready(function(){$("#secondnav").hide();window.onscroll=function(){changeScroll()}});